import { Text, View, StyleSheet, ScrollView, TextInput, KeyboardAvoidingView } from "react-native";

const MainMenu = () => {
	const [authenticated, setAuthenticated] = useState("");

	const authenticate = async () => {
		try {
			const response = await fetch(host + "/auth/loggeduser", {
				method: "GET",
				credentials: "include",
			});
			const json = await response.json();
			setAuthenticated(json);
		} catch (error) {
			console.log(error);
		}
	};

	useEffect(() => {
		authenticate();
	}, []);

	return (
		<View>{authenticated.email !== undefined ? <MainMenu /> : <AuthLogin setAuthenticated={setAuthenticated} />}</View>
	);
};

export default MainMenu;
