import { useState } from "react";
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Alert, Button } from "react-native";

const AuthLogin = ({ navigation }) => {
	const [userCredentials, setUserCredential] = useState({ email: "", password: "" });

	const url = "http://localhost:3000/auth/login";
	const headers = {
		Accept: "application/json",
		"Content-Type": "application/json",
		"Access-Control-Allow-Credentials": true,
	};
	const body = JSON.stringify(userCredentials);

	const login = async () => {
		try {
			const response = await fetch(url, { method: "POST", credentials: "include", headers: headers, body: body });
			Alert.alert("It works");
			navigation.navigate("/admin");
		} catch (error) {
			Alert.alert(error);
		}
	};

	return (
		// <View style={styles.container}>
		// 	<Text>Email </Text>
		// 	<TextInput style={styles.input} onChangeText={(v) => setUserCredential({ ...userCredentials, email: v })} />
		// 	<Text>Password </Text>
		// 	<TextInput style={styles.input} onChangeText={(v) => setUserCredential({ ...userCredentials, password: v })} />
		// 	<TouchableOpacity style={styles.sendButton} onPress={login}>
		// 		<Text style={{ color: "white" }}>Send</Text>
		// 	</TouchableOpacity>
		// </View>

		<View>
			<Text>Welcome back.</Text>
			<TextInput
				label="Email"
				returnKeyType="next"
				// value={email.value}
				// onChangeText={(text) => setEmail({ value: text, error: "" })}
				// error={!!email.error}
				// errorText={email.error}
				autoCapitalize="none"
				autoCompleteType="email"
				textContentType="emailAddress"
				keyboardType="email-address"
			/>
			<TextInput
				label="Password"
				returnKeyType="done"
				// value={password.value}
				// onChangeText={(text) => setPassword({ value: text, error: "" })}
				// error={!!password.error}
				// errorText={password.error}
				secureTextEntry
			/>
			<View style={styles.forgotPassword}>
				<TouchableOpacity
				// onPress={() => navigation.navigate("ResetPasswordScreen")}
				>
					<Text style={styles.forgot}>Forgot your password?</Text>
				</TouchableOpacity>
			</View>
			<Button
				mode="contained"
				// onPress={onLoginPressed}
			>
				Login
			</Button>
			<View style={styles.row}>
				<Text>Don't have an account? </Text>
				<TouchableOpacity
				// onPress={() => navigation.replace("RegisterScreen")}
				>
					<Text style={styles.link}>Sign up</Text>
				</TouchableOpacity>
			</View>
		</View>
	);
};

const styles = StyleSheet.create({
	forgotPassword: {
		width: "100%",
		alignItems: "flex-end",
		marginBottom: 24,
	},
	row: {
		flexDirection: "row",
		marginTop: 4,
	},
	forgot: {
		fontSize: 13,
		// color: theme.colors.secondary,
	},
	link: {
		fontWeight: "bold",
		// color: theme.colors.primary,
	},
});

