import { useState } from "react";
import { StyleSheet, Text, TextInput, TouchableOpacity, View } from "react-native";
import { io } from "socket.io-client";
const socket = io("localhost:3000");

const Room = ({ room }) => {
	const [currentNumber, setCurrentNumber] = useState(0);
	const emitValue = (number) => {
		socket.emit("emmitToRoom", { room: room, value: currentNumber });
	};

	return (
		<View style={styles.roomContainer}>
			<Text>Room Number {room}</Text>
			<TextInput style={styles.input} onChange={setCurrentNumber} />
			<TouchableOpacity style={styles.sendButton} onPress={emitValue}>
				<Text style={{ color: "white" }}>Send</Text>
			</TouchableOpacity>
		</View>
	);
};

const Rooms = () => {
	return (
		<View>
			<Room room={1} />
			<Room room={2} />
		</View>
	);
};

const styles = StyleSheet.create({
	container: {
		maxWidth: "100%",
		alignItems: "center",
		justifyContent: "center",
	},
	roomContainer: {
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "center",
	},
	input: {
		height: 40,
		margin: 12,
		borderWidth: 1,
		padding: 10,
	},
	sendButton: {
		backgroundColor: "green",
		height: 40,
		width: 80,
		alignItems: "center",
		justifyContent: "center",
	},
});

export default Rooms;
