import { StyleSheet, Text, View, TextInput, TouchableOpacity } from "react-native";

const AuthLogin = ({ navigation }) => {
	const [userCredentials, setUserCredential] = useState({ email: "", password: "" });

	const login = async () => {
		const response = await fetch("http://localhost:3000/auth/login", {
			method: "POST",
			headers: {
				Accept: "application/json",
				"Content-Type": "application/json",
			},
			body: JSON.stringify({ email: userCredentials.email, password: userCredentials.password }),
		});
		const json = await response.json();
		if (!json.ok) {
			const message = `An error has occured: ${json.status}`;
			throw new Error(message);
		} else {
			console.log(json);
			navigation.navigate("/admin");
		}
	};

	return (
		<View style={styles.container}>
			<Text>Email </Text>
			<TextInput style={styles.input} onChange={(v) => setUserCredential({ ...userCredentials, email: v })} />
			<Text>Password </Text>
			<TextInput style={styles.input} onChange={(v) => setUserCredential({ ...userCredentials, password: v })} />
			<TouchableOpacity style={styles.sendButton}>
				<Text style={{ color: "white" }}>Send</Text>
			</TouchableOpacity>
		</View>
	);
};

const styles = StyleSheet.create({
	container: {
		maxWidth: "100%",
		alignItems: "center",
		justifyContent: "center",
	},

	input: {
		height: 40,
		margin: 12,
		borderWidth: 1,
		padding: 10,
	},
	sendButton: {
		backgroundColor: "green",
		height: 40,
		width: 80,
		alignItems: "center",
		justifyContent: "center",
	},
});

export default AuthLogin;
