import { Text, View, StyleSheet, ScrollView, TextInput } from "react-native";
import { Picker } from "@react-native-picker/picker";
import { useState } from "react";

const Settings = () => {
	const [companies, setCompanies] = useState();

	return (
		<ScrollView contentContainerStyle={{ alignItems: "center" }}>
			<View style={styles.cardContainer}>
				<View style={{ marginTop: 30, marginBottom: 30 }}>
					<Text style={{ fontSize: 20, marginBottom: 10 }}>Select a company</Text>
					<Picker
						style={{ height: 30 }}
						selectedValue={companies}
						onValueChange={(itemValue, itemIndex) => setCompanies(itemValue)}
					>
						<Picker.Item label="Zelli" value="zelli" />
						<Picker.Item label="Tron" value="tron" />
					</Picker>
				</View>
				<View>
					<Text>Company Name</Text>
					<TextInput style={styles.input}></TextInput>
				</View>
				<View>
					<Text>Slug</Text>
					<TextInput style={styles.input}></TextInput>
				</View>
			</View>
		</ScrollView>
	);
};

const styles = StyleSheet.create({
	cardContainer: {
		width: "95%",
		maxWidth: 640,
		backgroundColor: "white",
		alignContent: "center",
		padding: 30,
		margin: 10,
		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 5,
		},
		shadowOpacity: 0.34,
		shadowRadius: 6.27,
		elevation: 10,
		borderRadius: 20,
	},
	input: { margin: 30, borderBottomColor: "#000000", borderBottomWidth: 1 },
});
export default Settings;
