import { Button, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
// import { createNativeStackNavigator } from "@react-navigation/native-stack";
import AuthLogin from "./components/admin/AuthLogin";
import AdminRooms from "./components/admin/AdminRooms";
import Client from "./components/Client";
import MainMenu from "./components/admin/MainMenu";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

// const Stack = createNativeStackNavigator();
const Tab = createBottomTabNavigator();

const Welcome = ({ navigation }) => {
	return (
		<View style={{ justifyContent: "center", alignItems: "center" }}>
			<Text>Choose a Room</Text>
			<TouchableOpacity
				style={{ alignItems: "center", backgroundColor: "#DDDDDD", padding: 10, margin: 10 }}
				onPress={() => {
					navigation.navigate("/admin");
				}}
			>
				<Text>Admin Rooms</Text>
			</TouchableOpacity>

			<TouchableOpacity
				style={{ alignItems: "center", backgroundColor: "#DDDDDD", padding: 10, margin: 10 }}
				onPress={() => {
					navigation.navigate("/room", { room: 1 });
				}}
			>
				<Text>Go to room 1</Text>
			</TouchableOpacity>

			<TouchableOpacity
				style={{ alignItems: "center", backgroundColor: "#DDDDDD", padding: 10, margin: 10 }}
				onPress={() => {
					navigation.navigate("/room", { room: 2 });
				}}
			>
				<Text>Go to room 2</Text>
			</TouchableOpacity>
		</View>
	);
};

export default function App() {
	return (
		<NavigationContainer>
			<Tab.Navigator>
				<Tab.Screen name="/" component={AdminRooms} options={{ title: "AdminRooms" }} />
				<Tab.Screen name="/room" component={Client} options={({ route }) => ({ title: "route.params.name" })} />
				<Tab.Screen name="/admin" component={AdminRooms} options={{ title: "Rooms Admin" }} />
				<Tab.Screen name="/mainmenu" component={MainMenu} options={{ title: "Main Menu" }} />
				<Tab.Screen name="/login" component={AuthLogin} />
			</Tab.Navigator>
		</NavigationContainer>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
		alignItems: "center",
		justifyContent: "center",
	},
});
