import { Text, View, StyleSheet, TextInput, TouchableOpacity } from "react-native";
import { useContext, useEffect, useState } from "react";
import { host } from "../../Constants";
import { AppContext } from "../../context";
import AsyncStorage from "@react-native-async-storage/async-storage";

const User = ({ u }) => {
	const [user, setUser] = useState(u);

	return (
		<View style={{ marginBottom: 10 }}>
			<View style={{ flexDirection: "row", marginBottom: 10 }}>
				<Text style={{ margin: 10, width: "20%" }}>User email</Text>
				<TextInput style={styles.input} />
			</View>
			<View style={{ flexDirection: "row", marginBottom: 10 }}>
				<Text style={{ margin: 10, width: "20%" }}>User Rol</Text>
				<TextInput style={styles.input}></TextInput>
			</View>
			<View style={{ justifyContent: "center", flexDirection: "row" }}>
				<TouchableOpacity style={[styles.sendButton, { marginRight: 5 }]}>
					<Text style={{ color: "white" }}>Save</Text>
				</TouchableOpacity>
				<TouchableOpacity style={[styles.sendButton, { marginRight: 5 }]}>
					<Text style={{ color: "white" }}>Delete</Text>
				</TouchableOpacity>
			</View>
		</View>
	);
};

const Users = () => {
	const [users, setUsers] = useState([{ id: "", email: "", role: "" }]);
	const getUsers = async () => {
		try {
			const companyId = await AsyncStorage.getItem("companyId");
			const response = await fetch(host + "/user/company/" + companyId, {
				method: "POST",
				headers: { Accept: "application/json", "Content-Type": "application/json" },
			});
			return await response.json();
		} catch (error) {
			console.log(error);
		}
	};

	useEffect(async () => {
		let u = await getUsers();
		setUsers(u);
	}, []);

	return (
		<View style={styles.cardContainer}>
			{users.map((u) => {
				return <User key={u.id} u={u} />;
			})}

			<User />
			<TouchableOpacity
				style={{
					backgroundColor: "black",
					borderRadius: 50,
					height: 40,
					width: 40,
					alignItems: "center",
					justifyContent: "center",
				}}
			>
				<Text style={{ color: "white" }}>+</Text>
			</TouchableOpacity>
		</View>
	);
};

const styles = StyleSheet.create({
	cardContainer: {
		width: "95%",
		maxWidth: 640,
		backgroundColor: "white",
		padding: 30,
		margin: 10,
		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 5,
		},
		shadowOpacity: 0.34,
		shadowRadius: 6.27,
		elevation: 10,
		borderRadius: 10,
	},
	input: {
		width: "80%",
		height: 40,
		borderWidth: 1,
		borderRadius: 5,
	},
	sendButton: {
		backgroundColor: "black",
		borderRadius: 5,
		height: 40,
		width: 80,
		alignItems: "center",
		justifyContent: "center",
	},
});

export default Users;
