import { Text, View, StyleSheet, ScrollView, TextInput } from "react-native";

const MainMenu = () => {
	return (
		<ScrollView contentContainerStyle={{ alignItems: "center" }}>
			<View style={styles.cardContainer}>
				<Text style={{ fontSize: 20 }}>Room 1</Text>
				<TextInput style={styles.input}></TextInput>
			</View>
		</ScrollView>
	);
};

const styles = StyleSheet.create({
	cardContainer: {
		width: "95%",
		maxWidth: 640,
		backgroundColor: "white",
		alignContent: "center",
		padding: 30,
		margin: 10,
		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 5,
		},
		shadowOpacity: 0.34,
		shadowRadius: 6.27,
		elevation: 10,
		borderRadius: 20,
	},
	input: { margin: 30, borderBottomColor: "#000000", borderBottomWidth: 1 },
});

export default MainMenu;
