import { initializeApp } from "firebase/app";

import { FIREBASE_CONFIG } from "./Constants";
const firebaseConfig = FIREBASE_CONFIG;
const firebaseApp = initializeApp(firebaseConfig);

import { useEffect, useState } from "react";
import { View, Text, TextInput, Button } from "react-native";
import { getAuth, signInWithEmailAndPassword, onAuthStateChanged, signOut } from "firebase/auth";

const auth = getAuth();

const LoginPage = ({ setUser }) => {
	const [email, setEmail] = useState("");
	const [pass, setPass] = useState("");

	const logIn = () => {
		signInWithEmailAndPassword(auth, email, pass)
			.then((userCredential) => {
				// Signed in
				const user = userCredential.user;
				console.log(user);
				setUser(user);
				// ...
			})
			.catch((error) => {
				const errorCode = error.code;
				const errorMessage = error.message;
				console.log(errorMessage);
			});
	};

	return (
		<View>
			<Text>email</Text>
			<TextInput style={{ borderStyle: "solid", borderBottomWidth: 1 }} onChangeText={setEmail}></TextInput>
			<Text>password</Text>
			<TextInput style={{ borderStyle: "solid", borderBottomWidth: 1 }} onChangeText={setPass}></TextInput>
			<Button title="Send" onPress={logIn} />
		</View>
	);
};

const Welcome = () => {
	const logout = () => {
		signOut(auth)
			.then(() => {
				// Sign-out successful.
				console.log("signed out");
				setUser(false);
			})
			.catch((error) => {
				// An error happened.
				console.log("failed signout");
			});
	};
	return (
		<View>
			<Text>welcome</Text>
			<Button onPress={logout} />
		</View>
	);
};

export default function App() {
	const [user, setUser] = useState(null);

	useEffect(
		onAuthStateChanged(auth, (user) => {
			console.log(user);
			if (user) {
				setUser(user);
				// ...
			} else {
				setUser(false);
				console.log("not loggedin");
			}
		}),
		[]
	);

	// return <>{authenticated.ok === true ? <NavigationTab /> : <LoginPage setAuthenticated={setAuthenticated} />}</>;
	// return <NavigationTab />
	return <>{user ? <Welcome /> : <LoginPage setUser={setUser} />}</>;
}
