import { StyleSheet, Text, TouchableOpacity, View } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import AdminRooms from "./components/admin/AdminRooms";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import Settings from "./components/Settings";
import Ionicons from "react-native-vector-icons/Ionicons";
import { useEffect, useState } from "react";
import AuthLogin from "./components/admin/AuthLogin";
import { host } from "./Constants";

const Tab = createBottomTabNavigator();

export default function App() {
	const [authenticated, setAuthenticated] = useState("");

	const authenticate = async () => {
		try {
			const response = await fetch(host + "/auth/loggeduser", {
				method: "GET",
				credentials: "include",
			});
			const json = await response.json();
			setAuthenticated(json);
		} catch (error) {
			console.log(error);
		}
	};

	useEffect(() => {
		authenticate();
	}, []);
	return (
		<NavigationContainer>
			<Tab.Navigator
				screenOptions={({ route }) => ({
					tabBarIcon: ({ focused, color, size }) => {
						let iconName;
						if (route.name === "/") iconName = "notifications-outline";
						else if (route.name === "/settings") iconName = "settings-outline";
						// You can return any component that you like here!
						return <Ionicons name={iconName} size={size} color={color} />;
					},
					tabBarActiveTintColor: "green",
					tabBarInactiveTintColor: "gray",
				})}
			>
				{authenticated.email !== undefined ? (
					<Tab.Screen name="/login" component={AuthLogin} />
				) : (
					<>
						<Tab.Screen name="/" component={AdminRooms} options={{ title: "AdminRooms" }} />
						<Tab.Screen name="/settings" component={Settings} options={{ title: "Settings" }} />
					</>
				)}
				{/* <Tab.Screen name="/" component={AdminRooms} options={{ title: "AdminRooms" }} />
				<Tab.Screen name="/settings" component={Settings} options={{ title: "Settings" }} />
				<Tab.Screen name="/login" component={AuthLogin} /> */}
			</Tab.Navigator>
		</NavigationContainer>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
		alignItems: "center",
		justifyContent: "center",
	},
});
