import { useEffect, useState } from "react";
import { StyleSheet, Text, TextInput, TouchableOpacity, View } from "react-native";
import { io } from "socket.io-client";
const socket = io("localhost:3000");

const Room = ({ room }) => {
	const [value, setValue] = useState("");
	const [userCredentials, setUserCredential] = useState({ email: "guazelli.ugo@gmail.com", password: "Gu4z3lli" });
	const emitValue = () => {
		socket.emit("emmitToRoom", { room: room, value: value });
	};

	useEffect(() => login(), []);

	const login = async () => {
		const response = await fetch("http://localhost:3000/auth/login", {
			method: "POST",
			headers: {
				Accept: "application/json",
				"Content-Type": "application/json",
			},
			body: JSON.stringify({ email: userCredentials.email, password: userCredentials.password }),
		});
		const json = await response.json();
		if (!json.ok) {
			const message = `An error has occured: ${json.status}`;
			throw new Error(message);
		} else {
			console.log(json);
		}
	};

	return (
		<View style={styles.roomContainer}>
			<Text>Room Number {room}</Text>
			<TextInput style={styles.input} value={value} onChangeText={setValue} />
			<TouchableOpacity style={styles.sendButton} onPress={emitValue}>
				<Text style={{ color: "white" }}>Send</Text>
			</TouchableOpacity>
		</View>
	);
};

const Rooms = () => {
	return (
		<View>
			<Room room={1} />
			<Room room={2} />
		</View>
	);
};

const styles = StyleSheet.create({
	container: {
		maxWidth: "100%",
		alignItems: "center",
		justifyContent: "center",
	},
	roomContainer: {
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "center",
	},
	input: {
		height: 40,
		margin: 12,
		borderWidth: 1,
		padding: 10,
	},
	sendButton: {
		backgroundColor: "green",
		height: 40,
		width: 80,
		alignItems: "center",
		justifyContent: "center",
	},
});

export default Rooms;
