import { useState } from "react";
import { View, Text, TextInput, StyleSheet, TouchableOpacity, Image } from "react-native";
import { AUTH } from "../../Constants";
import { signInWithEmailAndPassword } from "firebase/auth";

const LoginPage = ({ setUser }) => {
	const [email, setEmail] = useState("");
	const [pass, setPass] = useState("");

	const logIn = () => {
		signInWithEmailAndPassword(AUTH, email, pass)
			.then((userCredential) => {
				const user = userCredential.user;
				setUser(user);
			})
			.catch((error) => {
				const errorCode = error.code;
				const errorMessage = error.message;
				console.log(errorMessage);
			});
	};

	return (
		<View style={styles.container}>
			<View style={styles.cardContainer}>
				<View style={{ width: "100%", justifyContent: "center" }}>
					<Image source={require("../../assets/logo-react-icon.png")} style={{ width: 100, height: 100 }} />
				</View>

				<View style={{ marginBottom: 10 }}>
					<Text style={{ marginBottom: 10 }}>Email</Text>
					<TextInput style={styles.input} keyboardType="email-address" onChangeText={setEmail} />
				</View>

				<View style={{ marginBottom: 10 }}>
					<Text style={{ marginBottom: 10 }}>Password</Text>
					<TextInput style={styles.input} secureTextEntry={true} onChangeText={setPass} />
					<TouchableOpacity style={{ width: "100%", flexDirection: "row-reverse" }}>
						<Text style={{ marginTop: 10, marginBottom: 10 }}>
							Forgot your password? <Text style={{ color: "blue" }}>Click here to recover it.</Text>
						</Text>
					</TouchableOpacity>
				</View>

				<View style={{ width: "100%" }}>
					<TouchableOpacity style={styles.sendButton} onPress={logIn}>
						<Text style={{ color: "white" }}>Login</Text>
					</TouchableOpacity>
					<TouchableOpacity style={{ width: "100%", flexDirection: "row", justifyContent: "center" }}>
						<Text style={{ marginTop: 10, marginBottom: 10 }}>
							Don't have an account? <Text style={{ color: "blue" }}>Sign up.</Text>
						</Text>
					</TouchableOpacity>
				</View>

				{/*
				<Text>email</Text>
				<TextInput style={{ borderStyle: "solid", borderBottomWidth: 1 }} onChangeText={setEmail}></TextInput>
				<Text>password</Text>
				<TextInput style={{ borderStyle: "solid", borderBottomWidth: 1 }} onChangeText={setPass}></TextInput>
				<Button title="Send" onPress={logIn} /> */}
			</View>
		</View>
	);
};

const styles = StyleSheet.create({
	container: {
		width: "100%",
		height: "100%",
		justifyContent: "center",
		alignItems: "center",
	},
	cardContainer: {
		width: "95%",
		maxWidth: 480,
		backgroundColor: "white",
		padding: 30,
		margin: 10,
		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 5,
		},
		shadowOpacity: 0.34,
		shadowRadius: 6.27,
		elevation: 10,
		borderRadius: 10,
	},
	input: {
		width: "100%",
		height: 40,
		borderWidth: 1,
		borderRadius: 5,
		borderColor: "#CCCCCC",
		paddingLeft: 10,
	},
	sendButton: {
		backgroundColor: "#41BAEE",
		borderRadius: 5,
		height: 40,
		width: "100%",
		alignItems: "center",
		justifyContent: "center",
	},
});

export default LoginPage;
