import { initializeApp } from "firebase/app";

const firebaseApp = initializeApp({
	apiKey: "AIzaSyATcWgmO37ZVBePq_v_0fT6p1CRxUGDpQc",
	authDomain: "linestron-app.firebaseapp.com",
	databaseURL: "https://linestron-app-default-rtdb.firebaseio.com",
	projectId: "linestron-app",
	storageBucket: "linestron-app.appspot.com",
	messagingSenderId: "200383405318",
	appId: "1:200383405318:web:3a44dbb0c4d0b3ce555287",
	measurementId: "G-BJG6DK8GVT",
});

import { useEffect, useState } from "react";
import { View, Text, TextInput, Button } from "react-native";
import { getAuth, signInWithEmailAndPassword, onAuthStateChanged, signOut } from "firebase/auth";

const auth = getAuth();

const LoginPage = ({ setUser }) => {
	const [email, setEmail] = useState("");
	const [pass, setPass] = useState("");

	const logIn = () => {
		signInWithEmailAndPassword(auth, email, pass)
			.then((userCredential) => {
				// Signed in
				const user = userCredential.user;
				console.log(user);
				setUser(user);
				// ...
			})
			.catch((error) => {
				const errorCode = error.code;
				const errorMessage = error.message;
				console.log(errorMessage);
			});
	};

	return (
		<View>
			<Text>email</Text>
			<TextInput style={{ borderStyle: "solid", borderBottomWidth: 1 }} onChangeText={setEmail}></TextInput>
			<Text>password</Text>
			<TextInput style={{ borderStyle: "solid", borderBottomWidth: 1 }} onChangeText={setPass}></TextInput>
			<Button title="Send" onPress={logIn} />
		</View>
	);
};

const Welcome = () => (
	<View>
		<Text>welcome</Text>
	</View>
);

export default function App() {
	const [user, setUser] = useState(null);

	useEffect(
		onAuthStateChanged(auth, (user) => {
			if (user) {
				setUser(user);
				// ...
			} else {
				console.log("not loggedin");
			}
		}),
		[]
	);

	// return <>{authenticated.ok === true ? <NavigationTab /> : <LoginPage setAuthenticated={setAuthenticated} />}</>;
	// return <NavigationTab />
	return <>{user ? <Welcome /> : <LoginPage setUser={setUser} />}</>;
}
