import { useState } from "react";
import { View, Text, TouchableOpacity, StyleSheet, TextInput, Button } from "react-native";
import { host } from "../../Constants";

const LoginPage = ({ setUser }) => {
	const [email, setEmail] = useState("");
	const [pass, setPass] = useState("");

	const logIn = () => {
		signInWithEmailAndPassword(AUTH, email, pass)
			.then((userCredential) => {
				const user = userCredential.user;
				setUser(user);
			})
			.catch((error) => {
				const errorCode = error.code;
				const errorMessage = error.message;
				console.log(errorMessage);
			});
	};

	return (
		<View>
			<Text>email</Text>
			<TextInput style={{ borderStyle: "solid", borderBottomWidth: 1 }} onChangeText={setEmail}></TextInput>
			<Text>password</Text>
			<TextInput style={{ borderStyle: "solid", borderBottomWidth: 1 }} onChangeText={setPass}></TextInput>
			<Button title="Send" onPress={logIn} />
		</View>
	);
};

const styles = StyleSheet.create({
	cardContainer: {
		width: "95%",
		maxWidth: 480,
		backgroundColor: "white",
		padding: 30,
		margin: 10,
		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 5,
		},
		shadowOpacity: 0.34,
		shadowRadius: 6.27,
		elevation: 10,
		borderRadius: 10,
	},
	input: {
		height: 40,
		width: 200,
		margin: 12,
		borderWidth: 1,
		padding: 10,
		borderColor: "white",
		color: "white",
	},
	sendButton: {
		backgroundColor: "green",
		height: 40,
		width: 80,
		alignItems: "center",
		justifyContent: "center",
	},
});

export default LoginPage;
