import { useState } from "react";
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Alert } from "react-native";

const AuthLogin = ({ navigation }) => {
	const [userCredentials, setUserCredential] = useState({ email: "", password: "" });

	const url = "http://localhost:3000/auth/login";
	const headers = {
		Accept: "application/json",
		"Content-Type": "application/json",
		"Access-Control-Allow-Credentials": true,
	};
	const body = JSON.stringify(userCredentials);

	const login = async () => {
		try {
			const response = await fetch(url, { method: "POST", credentials: "include", headers: headers, body: body });
			navigation.navigate("/admin");
			console.log("passed navigation");
		} catch (error) {
			Alert.alert(error);
		}
	};

	return (
		<View style={styles.container}>
			<Text style={{ fontSize: 20, margin: 40, color: "white" }}>Login Page</Text>
			<Text style={{ color: "white" }}>Email </Text>
			<TextInput style={styles.input} onChangeText={(v) => setUserCredential({ ...userCredentials, email: v })} />
			<Text style={{ color: "white" }}>Password </Text>
			<TextInput style={styles.input} onChangeText={(v) => setUserCredential({ ...userCredentials, password: v })} />
			<TouchableOpacity style={styles.sendButton} onPress={login}>
				<Text style={{ color: "white" }}>Send</Text>
			</TouchableOpacity>
		</View>
	);
};

const styles = StyleSheet.create({
	container: {
		maxWidth: "100%",
		alignItems: "center",
		justifyContent: "center",
		backgroundColor: "#2D2D44",
		padding: 100,
	},
	input: {
		height: 40,
		margin: 12,
		borderWidth: 1,
		padding: 10,
		borderColor: "white",
		color: "white",
	},
	sendButton: {
		backgroundColor: "green",
		height: 40,
		width: 80,
		alignItems: "center",
		justifyContent: "center",
	},
});

export default AuthLogin;
