import { Text, View, StyleSheet, ScrollView, TextInput, TouchableOpacity } from "react-native";
import { Picker } from "@react-native-picker/picker";
import { useState } from "react";

const Settings = () => {
	const [companies, setCompanies] = useState();

	return (
		<ScrollView contentContainerStyle={{ alignItems: "center" }}>
			<View style={styles.cardContainer}>
				<View style={{ flexDirection: "row", marginBottom: 10 }}>
					<Text style={{ margin: 10, width: "20%" }}>Select a company</Text>
					<Picker
						style={styles.input}
						selectedValue={companies}
						onValueChange={(itemValue, itemIndex) => setCompanies(itemValue)}
					>
						<Picker.Item label="Zelli" value="zelli" />
						<Picker.Item label="Tron" value="tron" />
					</Picker>
				</View>
				<View style={{ flexDirection: "row", marginBottom: 10 }}>
					<Text style={{ margin: 10, width: "20%" }}>Company Name</Text>
					<TextInput style={styles.input}></TextInput>
				</View>
				<View style={{ flexDirection: "row", marginBottom: 10 }}>
					<Text style={{ margin: 10, width: "20%" }}>Slug</Text>
					<TextInput style={styles.input}></TextInput>
				</View>
				<View>
					<TouchableOpacity style={styles.sendButton}>
						<Text style={{ color: "white" }}>Send</Text>
					</TouchableOpacity>
				</View>
			</View>
		</ScrollView>
	);
};

const styles = StyleSheet.create({
	cardContainer: {
		width: "95%",
		maxWidth: 640,
		backgroundColor: "white",
		padding: 30,
		margin: 10,
		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 5,
		},
		shadowOpacity: 0.34,
		shadowRadius: 6.27,
		elevation: 10,
		borderRadius: 20,
	},
	input: {
		width: "80%",
		height: 40,
		borderColor: "green",
		borderWidth: 1,
	},
	sendButton: {
		backgroundColor: "green",
		height: 40,
		width: 80,
		alignItems: "center",
		justifyContent: "center",
	},
});
export default Settings;
